// API service kết nối backend
import axios from 'axios';

const BASE_URL = 'http://localhost:8000/api/v1'; // Sử dụng discovery thực tế

export const api = axios.create({
  baseURL: BASE_URL,
  timeout: 10000,
});

export const setAuthToken = (token) => {
  api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
};

// Employees
export const getEmployees = () => api.get('/employees');
export const addEmployee = (data) => api.post('/employees', data);
export const updateEmployee = (id, data) => api.put(`/employees/${id}`, data);
export const deleteEmployee = (id) => api.delete(`/employees/${id}`);

// Devices
export const getDevices = () => api.get('/devices');
export const addDevice = (data) => api.post('/devices', data);
export const updateDevice = (id, data) => api.put(`/devices/${id}`, data);
export const deleteDevice = (id) => api.delete(`/devices/${id}`);
export const getDeviceStatus = (id) => api.get(`/devices/${id}`);

// Attendance
export const getAttendanceHistory = (deviceId) => api.get(`/attendance/history/${deviceId}`);
export const getEmployeeAttendance = (employeeId) => api.get(`/attendance/employee/${employeeId}`);

// Network
export const getNetworkStatus = () => api.get('/network');

// Auth
export const login = (data) => api.post('/auth/login', data);
